// --- Data Structures ---
interface LearningObjective {
    text: string;
    icon: string; // Material icon name
}

interface InfoBox {
    type: 'key_takeaway' | 'pro_tip' | 'ethical_consideration' | 'gcp_vertex_focus';
    title: string;
    text: string;
    icon: string;
}

interface Paragraph {
    type: 'paragraph';
    text: string;
}

interface Heading {
    type: 'heading';
    level: 2 | 3 | 4; 
    text: string;
}

interface BulletList {
    type: 'list';
    items: string[];
}

interface KnowledgeCheckQuestion {
    id: string;
    question: string;
    answer?: string;
    answerShown?: boolean;
}

type ContentItem = Paragraph | Heading | BulletList | InfoBox;

interface Section {
  id: string;
  title: string;
  content: ContentItem[];
}

interface Chapter {
  id: string;
  shortTitle: string; // For sidebar navigation
  fullTitle: string; // For content area header
  learningObjectives?: LearningObjective[];
  sections: Section[];
  knowledgeCheck?: KnowledgeCheckQuestion[];
}

